import React from 'react'
import { useQuery, gql, useMutation } from '@apollo/client';

const Page = () => {
    const { data, loading, error } = useQuery(gql`
        query getComment($id: ID!){
            getComment(id: $id){
                comment
            }
    }`,{variables: {
        id: "2"
    }})
    const { data: commentsData } = useQuery(gql`
    query getComments{
        getComments {
            author
            comment
        }
    }`,{variables: {

    }})
    const [mutateFunction, { data: mutationData }] = useMutation(gql`
        mutation createComment($comment: String,  $author: String){
            createComment(commentInput: {comment: $comment, author: $author}){
                author
                comment
            }
    }`,{variables: {
    comment: "hi",
    author: "Riya"
}})

    const handleMutate = () => {
        mutateFunction({variables: {
            comment: "this is a comment",
            author: "Riya"
        }})
    }

    console.log(commentsData?.getComments)


    const displayAuthor = () => {
        commentsData?.getComments.map(commentData => {
           <div>commentData.author </div> 
        })

    }

    // console.log(commentsData.comment)
    // console.log(commentsData.author)

    return (
        <div>
            <h2>Comments</h2>
            <h4> </h4>
            {JSON.stringify(commentsData)}
            <button onClick={handleMutate}></button>
            {JSON.stringify(mutationData)}
        </div>
    )
}

export default Page
