        query getComment($id: ID!){
            getComment(id: $id){
                comment
            }
    }`,{variables: {
        id: "2"
    }})
    const { data: commentsData } = useQuery(gql`
    query getComments{
        getComments {
            author
            comment
        }
    }`,{variables: {

    }})
    const [mutateFunction, { data: mutationData }] = useMutation(gql`
        mutation createComment($comment: String,  $author: String){
            createComment(commentInput: {comment: $comment, author: $author}){
                author
                comment
            }
    }`,{variables: {
    comment: "hi",
    author: "Riya"
}})

    const handleMutate = () => {
        mutateFunction({variables: {
            comment: "this is a comment",
            author: "Riya"
        }})
    }

    console.log(commentsData?.getComments)


