    // }

   const displayComments = () => {
       <div>
            {commentsData?.getComments.map((commentData) => {
                {/* console.log(commentData.author) */}
                <h4>{commentData.author}</h4>
            })}
       </div>
   }

    return (
        <div>
            <h2>Comments</h2>

            {/* <h4> { commentsData?.getComments[1].author} </h4> */}
            {JSON.stringify(commentsData)}
            <button onClick={handleMutate}></button>
            {JSON.stringify(mutationData)}
